import { UserType } from '~shared/services/user-info/user-info-store.service';
export function idNumberValidator(getType) {
  return control => {
    if (getType() === UserType.GUEST && (!control.value || control.value.length <= 20)) {
      return null;
    }

    if (!control.value || /^[0-9]*$/.test(control.value) && control.value.length === 9) {
      return null;
    }

    return {
      idNumberInvalid: true
    };
  };
}
